  def index
    @translations = Translation.find :all
    @available_locales = I18n.valid_locales
  end
  
  def show
    @translation = Translation.find params[:id]
  end

  def edit
    @translations = Translation.find :all, :conditions => ["locale = ?", I18n.locale]
    if params[:locale_reference] && !params[:locale_reference].blank?
      @locale_reference = params[:locale_reference]
    elsif I18n.locale != I18n.default_locale
      @locale_reference = I18n.default_locale
    end
    @default_locale = I18n.locale
  end
  
  def list_untranslated
    required_model = params[:model].first.singularize.capitalize
    @model = Class.class_eval required_model
    model_translation = Class.class_eval required_model+"Translation"
    model_table = @model.table_name
    model_table_translation = model_translation.table_name
    @default_locale = I18n.locale
    @untranslated = []
    I18n.valid_locales.each do |locale|
      I18n.locale=locale
      @untranslated+=@model.all [:conditions => ["(SELECT COUNT(*) FROM ? WHERE ? = m.id) = 0", model_table_translation, model_table.singularize+"_id"], :order => :id]
    end
    I18n.locale=@default_locale
  #  @untranslated = @model.all [:conditions => ["(SELECT COUNT(*) FROM ? WHERE ? = m.id) = 0", model_table_translation, model_table.singularize+"_id"], :order => :locale]
  end
  
  def edit_dynamic
    required_model = params[:model].first.singularize.capitalize
    @model = Class.class_eval required_model
    @model_translation = Class.class_eval required_model+"Translation"
    @object = @model.find params[:id]
    @translate_fields = @model_translation.columns.reject{ |column| ['id','locale',"#{@model.table_name.singularize}_id"].include?(column.name) }
    @locales_translated = I18n.valid_locales
    @locale_translated = params[:translated] || I18n.locale
    @locales_reference = @object.translated_locales.reject { |locale| locale == @locale_translated }
    @locale_reference = params[:reference] || (@locales_reference.first if @locales_reference)
    @default_locale = I18n.locale
  end
  
  def update_dynamic
    session_locale = I18n.locale
    required_model = params[:model].first.singularize.capitalize
    @model = Class.class_eval required_model
    @object = @model.find params[:id]
    I18n.locale = params[:locale]
    if @object.update_attributes params[required_model]
      I18n.locale = session_locale
      flash[:notice] = I18n.t('translation.update.success').capitalize
      return redirect_to(:controller => @model, :action => 'show')
    else
      I18n.locale = session_locale
      flash[:error] = I18n.t('translation.update.failed').capitalize unless has_flash_error?
      return redirect_to(:action => 'edit_dynamic')
    end
  end
  
  def update
    @translation = Translation.find params[:id]
    if @translation.update_attributes params[:globalize_translation]
      flash[:notice] = I18n.t('translation.update.success').capitalize
      return redirect_to(:action => 'edit')
    else
      flash[:error] = I18n.t('translation.update.failed').capitalize unless has_flash_error?
    end
  end
end
